INSERT INTO `wp_postmeta` VALUES("410", "60", "frs_woo_product_tabs", "a:1:{i:0;a:3:{s:5:\"title\";s:20:\"Instructions for Use\";s:2:\"id\";s:24:\"tab-instructions-for-use\";s:7:\"content\";s:1406:\"<ul>\r\n<li>Do not use if the infant’s head can pass through the neck area when the product is fastened for use.</li>\r\n<li>Discontinue use if the shoulder snaps or zipper is damaged.</li>\r\n<li>Check regularly for signs of wear and tear.</li>\r\n<li>Discontinue use if worn or damaged.</li>\r\n<li>Discontinue use if the infant is able to unzip or unsnap the sleeping bag on his or her own.</li>\r\n<li>After machine drying, check the temperature of snaps before placing your baby in the baby sleeping bag.</li>\r\n<li>Use only when your baby is in the crib or cot.</li>\r\n<li>Discontinue use when your infant no longer sleeps in the crib or cot.</li>\r\n<li>Do not use the baby sleeping bag as a blanket or a loose covering.</li>\r\n<li>Never use the baby sleeping bag to attach your baby to the crib or cot.</li>\r\n<li>The baby sleeping bag is intended to be used over regular sleepwear, to replace additional bedding, such as loose blankets .</li>\r\n </ul>\r\n<h2>Care Instructions</h2>\r\n<ul>\r\n<li>Wash in cold water before use.</li>\r\n<li>Machine wash cold with similar colours on a gentle cycle.</li>\r\n<li>Do not bleach.</li>\r\n<li>Tumble dry on low heat.</li>\r\n<li>Low iron on reverse side if needed.</li>\r\n<li>Do not dry clean.</li>\r\n</ul>\r\n \r\n<h2>Choosing a Size</h2>\r\n<img class=\"alignleft size-full wp-image-109\" alt=\"sizing_chart\" src=\"http://sweetbabylilly.com.au/wp-content/uploads/2014/04/sizing_chart.png\" />\";}}");
